import { Validators } from '../../library/utilities/Validators';


export const loginBg = require('../../resources/images/login-bg.jpg');
export const loginImage = require('../../resources/images/loginBackground.png');

export const loginFormModel = [
  {
    label: 'Name',
    value: '',
    type: 'text',
    placeholder: '',
    field: 'name',
    validators: [
      { check: Validators.required, message: 'Name is mandatory' },
    ],
    required: true,
    styleClass: 'col-sm-12',
  },
  {
    label: 'Employee ID',
    value: '',
    type: 'text',
    placeholder: '',
    field: 'employeeId',
    validators: [{ check: Validators.required, message: 'Employee ID is mandatory' }],
    required: true,
    styleClass: 'col-sm-12',
  },

  {
    label: 'Email',
    value: '',
    type: 'text',
    placeholder: '',
    field: 'email',
    validators: [
      { check: Validators.required, message: 'Name is mandatory' },
      { check: Validators.email, message: 'Email is Invalid' },
    ],
    required: true,
    styleClass: 'col-sm-12',
  },

  {
    label: 'Date of Joining',
    value: '',
    type: 'date',
    placeholder: '',
    field: 'joiningDate',
    validators: [
      { check: Validators.required, message: 'Name is mandatory' },
    ],
    required: true,
    styleClass: 'col-sm-12',
  },
];
