import React, { Component, createRef } from 'react';
import { connect } from 'react-redux';
import { withRouter, Redirect } from 'react-router-dom';

import { getAuth, setAuthentication } from 'library/common/actions';
import { Button, Form } from 'library/common/components';

import {loginFormModel, loginImage, modalStyles} from './constants';
import './homePageStyles.scss';

import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

export class HomePage extends Component {
  formRef = createRef();

  state = {
    isLoading: false,
    formError: null,
    isFormVisible: false
  };

  componentDidMount() {
    document.title = 'Login';
    this.props.getAuth();
  }

  handleClick = async e => {
      e.preventDefault();
      const values = this.formRef.getFormData();
      const { formData, isFormValid } = values;
      if (isFormValid) {
        console.log(formData);
        // this.setState({isLoading: true});
      } else {
        this.setState({isLoading: false, formError: 'Please check form'});
      }
  };

  render() {
    const { isLoading, formError } = this.state;
    const classes = modalStyles();

    return (
        <div className="vh-100 login bg-white d-flex justify-content-center align-items-center">
          <div className="col-5 login-background vh-100 p-0">
            <img src={loginImage} alt="background" />
          </div>
          <div className="col-7 login-form vh-100 d-flex justify-content-center align-items-center">
            <div className="col-sm-5">
              <h4 className="pb-4">Sign In</h4>
              {!!formError && <p className="text-danger">{formError}</p>}
              <form onSubmit={this.handleLogin}>
                <Form
                    ref={el => {
                      this.formRef = el;
                    }}
                    model={loginFormModel}
                />
                <Button
                    type="submit"
                    loading={isLoading}
                    onClick={this.handleClick}
                    styleClass="btn-success btn-block"
                    value={'Submit'}
                />
              </form>
            </div>
          </div>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper}>
                        <h2 id="transition-modal-title">Transition modal</h2>
                        <p id="transition-modal-description">react-transition-group animates me.</p>
                    </div>
                </Fade>
            </Modal>
        </div>
    );
  }
}

const mapStateToProps = ({ authReducer }) => {
  return {

  };
};

export default withRouter(connect(mapStateToProps, { getAuth, setAuthentication })(HomePage));
