import React, {Component, createRef} from 'react';
import {connect} from 'react-redux';
import {withRouter, Redirect} from 'react-router-dom';
import {Button, Form} from 'library/common/components';

import {employeeFormModel, loginImage, employeeTableModel} from './constants';
import './homePageStyles.scss';

import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import MaterialTable from "material-table";
import {tableIcons} from "../../library/common/constants/TableIconsConstants";


export class HomePage extends Component {
    formRef = createRef();

    state = {
        isLoading: false,
        formError: null,
        isFormVisible: false,
        employeeData: [],
    };

    componentDidMount() {
        document.title = 'HomePage';
    }

    handleAdd = () => {
        this.setState({isFormVisible: true})
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const values = this.formRef.getFormData();
        const {formData, isFormValid} = values;
        if (isFormValid) {
            console.log(formData);
            let data = this.state.employeeData;
            data.push(formData);
            this.setState({employeeData: data}, () => {
                this.handleClose()
            })
            // this.setState({isLoading: true});
        } else {
            this.setState({isLoading: false, formError: 'Please check form'});
        }
    };

    handleClose = () => {
        this.setState({isFormVisible: false})
    };

    removeByAttr = (arr, attr, value) =>{
        let i = arr.length;
        while(i--){
            if( arr[i]
                && arr[i].hasOwnProperty(attr)){
                arr.splice(i,1);
            }
        }
        this.setState({employeeData: arr});
    };

    render() {
        const {isLoading, formError, isFormVisible, employeeData} = this.state;


        return (
            <div className="vh-100 login bg-white d-flex justify-content-center align-items-center">
                <div className="col-5 login-background vh-100 p-0">
                    <img src={loginImage} alt="background"/>
                </div>
                <div className="col-7 login-form vh-100 d-flex justify-content-center align-items-center">
                    <div className='row align-items-center justify-content-center'>
                        {employeeData.length > 0 && <MaterialTable
                            icons={tableIcons}
                            columns={employeeTableModel}
                            data={employeeData}
                            actions={[
                                {
                                    icon: 'delete',
                                    tooltip: 'Delete User',
                                    onClick: (event, rowData) => this.removeByAttr(employeeData, 'name', rowData.name)
                                }
                            ]}
                            title="Employees Data"
                        /> }
                        <Button
                            type="submit"
                            loading={isLoading}
                            onClick={this.handleAdd}
                            styleClass="btn-success btn-block btn-style"
                            value={'Add New Employee'}
                        />
                    </div>
                </div>
                <Modal
                    aria-labelledby="transition-modal-title"
                    aria-describedby="transition-modal-description"
                    className='modal'
                    open={isFormVisible}
                    onClose={this.handleClose}
                    closeAfterTransition
                    BackdropComponent={Backdrop}
                    BackdropProps={{
                        timeout: 500,
                    }}
                >
                    <Fade in={isFormVisible}>
                        <div className="col-sm-5 paper">
                            <h4 className="pb-4">New Employee Details</h4>
                            {!!formError && <p className="text-danger">{formError}</p>}
                            <form onSubmit={this.handleSubmit}>
                                <Form
                                    ref={el => {
                                        this.formRef = el;
                                    }}
                                    model={employeeFormModel}
                                />
                                <Button
                                    type="submit"
                                    loading={isLoading}
                                    onClick={this.handleSubmit}
                                    styleClass="btn-success btn-block"
                                    value={'Submit'}
                                />
                            </form>
                        </div>
                    </Fade>
                </Modal>
            </div>
        );
    }
}

const mapStateToProps = () => {
    return {};
};

export default withRouter(connect(mapStateToProps, null)(HomePage));
